import React from 'react'

export default function TodoInsert() {

  const [lists, setLists] = useState([]);

  const onInsert = useCallback(e =>{
    const nextLists = lists;
    setLists(nextLists);
    setNumber('');
  },[number,lists]);

  return (
    <form>
      <input placeholder='할일을 입력하세요'/>
      <button onClick={onInsert}> + </button>
    </form>
  )
}
